package basic_prg;
import java.util.*;

public class If_condition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// if condition
			Scanner sc = new Scanner (System.in);
			int age = sc.nextInt();
		if(age<18) {
			System.out.println("not adult");
		}
		else {
			System.out.println("adult");
			}

	}

}
